
public class main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/* Un incremento en una variable en una variable es sumar y un decremento por consecuente seria restar  ++/-- */

	int numero = 5;
	
	numero--;
	numero--;
	numero++;
	
	System.out.println("Numero es igual a : "+numero);
	}

}
