
public class main {

	public static void main(String[] args) {
		// TODO Auto-generated method stu
		
		/* Switch case es una estructura de control que permite definir 
		 * varios casos de selección.
		 */
		
		int var =2;
		String cadena = "g";
		
		switch(cadena) {
		case "A":
			
			System.out.println("La variable es A");
			
			break;
		
		case "B":
			
			System.out.println("La variable contiene el B");
			
			break;
			
		case "C":
			
			System.out.println("La variable contiene C");
			
			break;
		
		default:
			
			System.out.println("Introduzca caracter correcto");
		
			
		}

	}

}
